import React, {useState} from 'react'
import {DropdownButton, Dropdown, Form} from 'react-bootstrap'

function KeyRequest() {

    const [dropdownValue, setDropdownValue] = useState('');
    const handleSelect = (e) => {
        setDropdownValue(e);
    }
    
    return (
        <div>
            {/* <DropdownButton
                id="dropdown-button-dark-example2"
                variant="secondary"
                menuVariant="dark"
                title="Select Key"
                className="mt-2"
                onSelect={handleSelect}
            >
                <Dropdown.Item eventKey="dukpt">DUKPT</Dropdown.Item>
                <Dropdown.Item eventKey="mskey">Master - Session Key</Dropdown.Item>
                <Dropdown.Item eventKey="aes">AES</Dropdown.Item>
                <Dropdown.Item eventKey="rsakeys">RSA Keys</Dropdown.Item>
            </DropdownButton> */}
            <Form.Select aria-label="Default select example">
                <option>Select Key</option>
                <option value="dukpt" onSelect={handleSelect}>DUKPT</option>
                <option value="mskey" onSelect={handleSelect}>Master - Session Key</option>
                <option value="aes" onSelect={handleSelect}>AES</option>
                <option value="rsakeys" onSelect={handleSelect}>RSA Keys</option>
            </Form.Select>
            {dropdownValue === "dukpt" && (
                <h3>Dukpt Form</h3>
            ) 
            }
            {dropdownValue === "mskey" && (
                <h3>Master Session Key</h3>
            ) 
            }
            {dropdownValue === "aes" && (
                <h3>AES</h3>
            ) 
            }
            {dropdownValue === "rsakeys" && (
                <h3>RSA Keys</h3>
            ) 
            }
        </div>
    )
}

export default KeyRequest;
