import React, {useState} from 'react'
import {DropdownButton, Dropdown, Form, Button} from 'react-bootstrap'

function KeyRequest() {

    const [dropdownValue, setDropdownValue] = useState('');
    const handleSelect = (e) => {
        setDropdownValue(e);
    }
    
    return (
        <div>
            <DropdownButton
                id="dropdown-button-dark-example2"
                variant="secondary"
                menuVariant="dark"
                title="Select Key"
                className="mt-2"
                onSelect={handleSelect}
                style={
                    {
                        position: "fixed",
                        top: "15%",
                        left: "5%"
                    }
                }
            >
                <Dropdown.Item eventKey="dukpt">DUKPT</Dropdown.Item>
                <Dropdown.Item eventKey="mskey">Master - Session Key</Dropdown.Item>
                <Dropdown.Item eventKey="aes">AES</Dropdown.Item>
                <Dropdown.Item eventKey="rsakeys">RSA Keys</Dropdown.Item>
            </DropdownButton>
            {dropdownValue === "dukpt" && (
                <> 
                    <Form style={
                        {
                            position: "fixed", 
                            top: "45%",
                            left: "45%"
                        }
                    }>
                        <Form.Group className="mb-3" controlId="dukptForm">
                            <Form.Label style={{color: "white"}}>Key</Form.Label>
                            <Form.Control type="text" placeholder="Enter" />
                        </Form.Group>
                        <Button variant="secondary" type="submit">
                            Submit
                        </Button>
                    </Form>
                </>
            ) 
            }
            {dropdownValue === "mskey" && (
                <>
                    <br />
                    <br />
                    <h3>Master Session Key</h3>
                </>
            ) 
            }
            {dropdownValue === "aes" && (
                <>
                    <br />
                    <br />
                    <h3>AES</h3>
                </>
            ) 
            }
            {dropdownValue === "rsakeys" && (
                <>
                    <br />
                    <br />
                    <h3>RSA Keys</h3>
                </>
            ) 
            }
        </div>
    )
}

export default KeyRequest;
